import React, { useState, useEffect } from "react";
import axios from "axios";
import styles from "./RegulationFilter.module.css";

const RegulationFilterPage = () => {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [subCategories, setSubCategories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch categories on component mount
  useEffect(() => {
    setLoading(true);
    axios
      .get("/api/categories")  // Use the new endpoint
      .then((response) => {
        setCategories(response.data);
        setError(null);
      })
      .catch((error) => {
        console.error("Error fetching categories:", error);
        setError("Failed to fetch categories. Please try again later.");
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  // Fetch sub-categories when a category is selected
  useEffect(() => {
    if (selectedCategory) {
      setLoading(true);
      axios
        .get(`/api/regulation-filter?category=${selectedCategory}`)  // Use the new endpoint
        .then((response) => {
          setSubCategories(response.data.map(item => item.sub_category));
          setError(null);
        })
        .catch((error) => {
          console.error("Error fetching sub-categories:", error);
          setError("Failed to fetch sub-categories. Please try again later.");
        })
        .finally(() => {
          setLoading(false);
        });
    } else {
      setSubCategories([]); // Reset sub-categories if no category is selected
    }
  }, [selectedCategory]);

  return (
    <div className={styles.appContainer}>
      <h1>LandLytics Database Search</h1>
      {loading && <p>Loading...</p>}
      {error && <p className={styles.error}>{error}</p>}
      <div className={styles.dropdownContainer}>
        <div className={styles.dropdown}>
          <label>Category</label>
          <select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
          >
            <option value="">Select Category...</option>
            {categories.map((category) => (
              <option key={category} value={category}>
                {category}
              </option>
            ))}
          </select>
        </div>
        {selectedCategory && (
          <div className={styles.dropdown}>
            <label>Sub-Category</label>
            <select>
              <option value="">Select Sub-Category...</option>
              {subCategories.map((subCategory) => (
                <option key={subCategory} value={subCategory}>
                  {subCategory}
                </option>
              ))}
            </select>
          </div>
        )}
      </div>
    </div>
  );
};

export default RegulationFilterPage;
